INSERT INTO `wl_news` (`id`, `title`, `picture`, `content`, `newstype`, `viewcount`, `addtime`)
VALUES
	(2,'a南昌为力人力资源招聘会计一名！待遇优厚！','','<p><strong>招聘岗位：会计。</strong></p><p><strong>招聘人数：1人</strong></p><p><strong>招聘单位：南昌为力人力资源有限公司</strong></p><p><strong>岗位要求：</strong></p><p><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span>&nbsp;</span><strong>&nbsp;&nbsp; 1、</strong><strong>两年工作经验及以上会计从业经验。</strong></p><p><strong>&nbsp; &nbsp; &nbsp; &nbsp; 2、统招大专以上学历。</strong></p><p><strong>&nbsp; &nbsp; &nbsp; &nbsp; 3、</strong><strong>有人力资源公司和培训学校会计从业经验优先。</strong><strong><span></span></strong></p><p><strong><br></strong></p><p><strong>薪资待遇：</strong></p><p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1、基本薪资面议</strong></p><p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong><strong>2、缴纳五险</strong></p><p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 3、享受国家法定节假日、福利</strong></p>','公司动态',53,'1551449048'),
	(3,'b南昌为力人力2019春节放假安排','','<p><span>尊敬的新老客户及全体员工：<br><br>又是一年新来到!惜别2018，我们迎来了充满希望、机遇和挑战的2019!<br><br>在此，感谢大家在过去一年里对南昌为力人力的支持与信任。<br><br>同时也希望在新的一年里，南昌为力人力资源有限公司能继续得到大家的关注及支持，并且南昌为力人力也会一如既往地为大家提供更优质的服务!<br><br>正值中国的传统节日“春节”来临，在此向各位新老客户和广大朋友们拜个早年!预祝大家新春愉快!财源广进!事业红火!</span></p><p><br></p><div style=\"text-align: center;\"><span><img src=\"http://www.jxwlrl.com/up/pic/201901/24/20190124113538549787686.jpg\" alt=\"\"></span></div><p><span><br>现将公司春节放假时间通知如下：</span><span></span></p><p><br></p><p><span><span>2019年春节时间：2019年2月5日</span><br><br><span>2019年春节放假安排</span><br><br><span>2019年2月4日(除夕)~2月10日 共7天。</span><br><br><span>调休安排</span><br><br><span>2019年2月2日(星期六)、3日(星期天)、11日(星期一)上班。</span><br><br><br>由于放假给大家带来的不便，敬请谅解!<br><br><br>再次感谢大家一直以来对南昌为力人力的关注及支持!<br><br>衷心祝愿大家度过一个欢乐、祥和、喜庆的春节!<br><br>南昌为力人力资源服务有限公司<br><br>2019年1月24日</span></p>','公司动态',42,'1551917289'),
	(9,'关于印发《江西省服务外包产业重点发展领域指导目录》的通知','','<p><span>各设区市、省直试点管县（市）商务主管部门：</span><br><br><span>　　为落实《国务院关于促进服务外包产业加快发展的意见》（国发〔2014〕67号）和《江西省人民政府关于促进服务外包产业加快发展的实施意见》（赣府发〔2015〕19号）精神，进一步明确服务外包产业发展导向，培育对外贸易竞争新优势，根据商务部等部门编制的《服务外包产业重点发展领域指导目录》，现结合江西实际印发《江西省服务外包产业重点发展指导目录》（以下简称指导目录）。在列入《指导目录》的项目中按年度扶持一批守法经营、信誉良好、具备较强国际竞争力的服务外包品牌企业，进而带动江西省服务外包整体发展水平的进一步提升。</span><br><br><span>　　</span><br><span>　　　附件：江西省服务外包产业重点发展领域指导目录</span><br><br><span>&nbsp;</span><br><br><span>　　　　　　　　　　　　　　　　　　　　&nbsp; 　&nbsp; 江西省商务厅</span><br><br><span>　　　　　　　　　　　　　　　　　　　&nbsp; 　　 2016年12月23日</span><br><br><span>　　</span><br><br><span>附件：</span><br><br><span>　　服务外包产业重点发展领域指导目录</span><br><br><span>　　　　</span><br><br><span>　　 本目录共涉及24个重点发展领域。其中，10个领域属于信息技术外包（ITO）范畴、6个领域属于业务流程外包（BPO）范畴，8个领域属于知识流程外包（KPO）范畴。</span><br><br><span>　　一、工业设计服务</span><br><br><span>　　定义和范围：工业设计服务是指提供专业的工业产品设计服务整体解决方案，或产品策划、外观造型设计及产品包装、产品展示等某一业务流程的服务。工业设计服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括外观设计、结构设计、试验认证、环境设计、工业生产线设计等5个业务类型。</span><br><br><span>　　主要应用领域：重点应用于制造业，批发和零售业，交通运输、仓储和邮政业，信息传输、软件和信息技术服务业，建筑业等5个国民经济行业。</span><br><br><span>　　二、数据分析服务</span><br><br><span>　　定义和范围：数据分析服务是指借助大数据技术，在对不同类型数据充分挖掘的基础上提供企业生产、营销、研发等各个环节的支撑服务，包括提供借助描述性统计及交叉分析等手段，了解客户业务发展过去、现状及存在的问题，为客户营销做基本支撑的数据分析服务；提供对商业数据库中的大量业务数据进行抽取、转换、分析和其他模型化处理，从中提取关键性数据，为客户商业决策提供支持的数据挖掘服务。数据分析服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括数据分析、数据挖掘2个业务类型。</span><br><br><span>　　主要应用领域：重点应用于制造业，电力、热力、燃气及水生产和供应业，批发和零售业，交通运输、仓储和邮政业等4个国民经济行业。</span><br><br><span>　　三、云计算服务</span><br><br><span>　　定义和范围：云计算服务是将大量用网络连接的计算资源统一管理和调度，构成一个计算资源池向用户提供按需服务，包括提供云主机、云空间、云开发、云测试和综合类服务产品。用户通过网络以按需、易扩展的方式获得所需资源和服务。随着云计算应用的加快发展，基于云计算等新一代信息技术的专业服务提供商将为客户提供公有云、私有云和混合云等服务。云计算服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括软件即服务（Software-as-a-Service，SaaS）、平台即服务（Platform-as-a-Service，PaaS）以及基础设施即服务（Infrastructure-as-a-Service，IaaS）等3个业务类型。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业，制造业，金融业，批发和零售业，交通运输、仓储和邮政业等5个国民经济行业。</span><br><br><span>　　四、医药和生物技术研发服务</span><br><br><span>　　定义和范围：医药和生物技术研发服务是指医药或相关企业将研发类业务外包给第三方专业机构完成，包括制药、生物医药、医疗器械等方面。医药和生物技术研发服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括药物产品开发、临床前试验及临床试验、药物注册、国际认证及产品上市辅导服务、产业化技术咨询服务等5个业务类型。</span><br><br><span>　　主要应用领域：重点面向制造业，科学研究和技术服务业，卫生和社会工作等3个国民经济行业。</span><br><br><span>　　五、集成电路和电子电路设计服务</span><br><br><span>　　定义和范围：集成电路和电子电路设计服务是指集成电路产品和电子电路产品的设计及相关技术支持服务。集成电路和电子电路设计服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括集成电路产品设计及相关技术支持服务、电子电路产品设计及相关技术支持服务2个业务类型。</span><br><br><span>　　主要应用领域：重点应用在制造业，信息传输、软件和信息技术服务业2个国民经济行业。</span><br><br><span>　　六、检验检测服务</span><br><br><span>　　定义和范围：检验检测服务是指为满足客户内部对采购物品、生产产品检测的需求而提供的专业服务，以及满足医疗机构诊断需要的第三方检测服务，不包括法律法规规定强制检测的部分。检验检测服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括第三方医学检验检测服务、第三方食品检验检测服务、第三方消费用品检验检测服务、第三方工业产品检验检测服务等4个业务类型。</span><br><br><span>　　主要应用领域：重点应用于制造业，卫生和社会工作，电力、热力、燃气及水生产和供应业等3个国民经济行业。</span><br><br><span>　　七、新能源技术研发服务</span><br><br><span>　　定义和范围：新能源技术研发服务是指核电、太阳能、风电、生物质能、储能和智能电网等新能源企业将研发类业务外包给第三方机构完成，为其提供专业的设备制造技术研发、工程技术研发和产品应用技术研发等服务。新能源技术研发服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括设备制造技术研发、工程技术研发、产品应用技术研发等3个业务类型。</span><br><br><span>　　主要应用领域：重点应用在电力、热力、燃气和水生产和供应业，信息传输、软件和信息技术服务业，制造业，科学研究与技术服务业等4个国民经济行业。</span><br><br><span>　　八、文化创意服务</span><br><br><span>　　定义和范围：文化创意服务是经济全球化背景下产生的以文化领域创造力为核心的业务活动，强调一种主体文化或文化因素通过技术、创业和产业化的方式开发、营销知识产权的业务，主要包括广播影视、动漫、音像、传媒、视觉艺术、表演艺术等服务。文化创意服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括文化软件服务、建筑设计服务、专业设计服务、广告设计服务等4个业务类型。其中，不包括工业设计服务中涉及文化创意服务的部分；文化软件服务包含动漫及网游设计研发服务和影视文化服务。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业，制造业，文化、体育和娱乐业，批发和零售业等4个国民经济行业。</span><br><br><span>　　九、工程技术服务</span><br><br><span>　　定义和范围：工程技术服务是指对工程项目的建设提供有技术依据的设计方案，如具体规划或总体设计，以及工程建设项目决策和管理提供咨询等服务，但不包括具体的施工活动及工程管理。工程技术服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括工程咨询、规划设计2个业务类型。</span><br><br><span>　　主要应用领域：重点面向制造业，电力、热力、燃气及水生产和供应业，建筑业，房地产业，交通运输、仓储和邮政业等5个国民经济行业。</span><br><br><span>　　十、专业业务服务</span><br><br><span>　　定义和范围：专业业务服务是指为客户提供特有专业技术的服务，包括提供具有垂直行业专业特性的业务环节或流程外包、特种精密设备及仪器的维护维修等服务，但不包括人力资源管理服务、物流方案设计等内容。专业业务服务属于业务流程外包（BPO）。</span><br><br><span>　　主要业务类型：主要包括金融服务专业业务服务、医疗服务专业业务服务、仪器设备维修服务、财务与会计审计服务等4个业务类型。</span><br><br><span>　　主要应用领域：重点应用于金融业，卫生和社会工作，制造业，电力、热力、燃气及水生产和供应业，信息传输、软件和信息技术服务业等5个国民经济行业。</span><br><br><span>　　十一、软件技术服务</span><br><br><span>　　定义和范围：软件技术服务是指软件咨询、维护、培训等技术性服务。软件技术服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括软件咨询、软件维护、软件培训、软件测试等4个业务类型。</span><br><br><span>　　主要应用领域：重点应用于包括信息传输、软件和信息技术服务业，制造业，电力、热力、燃气及水生产和供应业，金融业，交通运输、仓储和邮政业，卫生和社会工作，教育，文化、体育和娱乐业等8个国民经济行业。</span><br><br><span>　　十二、软件研发及开发服务</span><br><br><span>　　定义和范围：软件研发及开发服务是指根据用户要求建造出软件系统或者系统中软件部分的一个产品开发的过程，为用户的运营、生产、供应链、客户关系、人力资源和财务管理、计算机辅助设计、工程等业务进行软件开发，涵盖需求获取、需求分析、设计、编程、软件测试、版本控制的系统工程。软件研发及开发服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括定制软件研发、嵌入式软件研发、套装软件研发、系统软件研发等4个业务类型。</span><br><br><span>　　主要应用领域：重点应用于包括信息传输、软件和信息技术服务业，制造业，电力、热力、燃气及水生产和供应业，金融业，交通运输、仓储和邮政业，卫生和社会工作，教育，文化、体育和娱乐业等8个国民经济行业。</span><br><br><span>　　十三、信息系统运营和维护服务</span><br><br><span>　　定义和范围：信息系统运营和维护服务是指为客户提供特定技术或业务需求解决方案的规划、设计、实施和进行项目管理；提供涉及与外包一个机构的网络通信基础设施、客户端/服务器的一个或多个要素的支持和管理相关的一系列活动；提供信息工程、地理信息系统、远程维护等信息系统应用服务。信息系统运营和维护服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括信息系统集成、网络管理、桌面管理与维护、信息系统应用服务等4个业务类型。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业，制造业，电力、热力、燃气及水生产和供应业，公共管理、社会保障和社会组织，金融业，批发和零售业，交通运输、仓储和邮政业等7个国民经济行业。</span><br><br><span>　　十四、基础信息技术运营和维护服务</span><br><br><span>　　定义和范围：基础信息技术运营和维护服务是指为客户提供管理平台整合、信息技术基础设施管理、灾备中心、托管服务、安全服务、数字内容服务等基础信息技术的运营和维护服务。基础信息技术运营和维护服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括管理平台整合、信息技术基础设施管理、数据中心、托管服务、安全服务、数字内容服务等6个业务类型。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业，制造业，电力、热力、燃气及水生产和供应业，公共管理、社会保障和社会组织，金融业,批发和零售业，交通运输、仓储和邮政业等7个国民经济行业。</span><br><br><span>　　十五、电子商务平台服务</span><br><br><span>　　定义和范围：电子商务平台服务是指为客户搭建电子商务平台并提供技术支持，如为客户提供电子商务平台的规划、开发、测试、维护及运营服务。电子商务平台服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括电子商务平台开发、电子商务平台代运维2个业务类型。</span><br><br><span>　　主要应用领域：重点应用于制造业，信息传输、软件和信息技术服务业，批发和零售业，农业，租赁和商务服务业，交通运输、仓储和邮政业等6个国民经济行业。</span><br><br><span>　　十六、数据处理服务</span><br><br><span>　　定义和范围：数据处理服务是指为客户提供数据采集、录入、存储、检索、加工、变换等服务，包括纸面文档电子化转换以及影响文件管理等服务；提供表格、半结构化文档等的数据化处理，将各类数字资料电子化；为客户存储资料，并提供检索服务；为客户提供统一数据结构、筛查错误数据、对错误数据进行修复、改善数据完整性等服务，提高数据库的使用率。但本定义的数据处理业务不包括数据分析及挖掘服务。数据处理服务属于业务流程外包（BPO）。</span><br><br><span>　　主要业务类型：主要包括数据采集与录入、数据存储及检索、数据加工等3个业务类型。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业，制造业，金融业，批发和零售业，交通运输、仓储和邮政业，租赁和商务服务业，公共管理、社会保障和社会组织，电力、热力、燃气及水生产和供应业等8个国民经济行业。</span><br><br><span>　　十七、互联网营销推广服务</span><br><br><span>　　定义和范围：互联网营销推广服务是指借助互联网、移动互联网平台为客户优化营销推广渠道，从而辅助客户实现营销目标，包括营销方案设计、互联网媒体筛选、传播内容策划及效果监测等，但不包括电子商务平台的开发建设运营、产品的授权销售、数字内容等服务。互联网营销推广服务属于业务流程外包（BPO）。</span><br><br><span>　　主要业务类型：主要包括互联网营销方案设计、传播内容策划及效果监测、互联网媒体等3个业务类型。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业与制造业2个国民经济行业。</span><br><br><span>　　十八、信息技术解决方案服务</span><br><br><span>　　定义和范围：信息技术解决方案服务是指为用户提供面向行业特殊业务流程的专业解决方案，特别是为满足企业对新技术的需求，为其提供包含从方案设计、执行、测试、运营维护等整套业务流程内容。信息技术解决方案服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括信息化解决方案、智能化解决方案2个业务类型。</span><br><br><span>　　主要应用领域：重点应用于信息传输、软件和信息技术服务业，制造业，电力、热力、燃气及水生产和供应业，批发和零售业，公共管理、社会保障和社会组织等5个国民经济行业。</span><br><br><span>　　十九、新能源技术解决方案服务</span><br><br><span>　　定义和范围：新能源技术解决方案服务是指为核电、太阳能、风电、生物质能、储能和智能电网等新能源用户提供面向行业特殊业务流程的专业解决方案，特别是满足企业对新能源技术的需求，为其提供方案设计、执行、测试和运营维护等服务。新能源技术解决方案服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括新能源信息化解决方案、智能化解决方案2个业务类型。</span><br><br><span>　　主要应用领域：重点应用在电力、热力、燃气及水生产和供应业，信息传输、软件和信息技术服务业，制造业，科学研究与技术服务业等4个国民经济行业。</span><br><br><span>　　二十、供应链管理服务</span><br><br><span>　　定义和范围：供应链管理服务是指为客户提供供应链方案设计与物流方案设计服务，包括对整个供应链系统进行计划、协调、操作、控制和优化的各种活动和过程的服务。供应链管理服务属于业务流程外包（BPO）。</span><br><br><span>　　主要业务类型：主要包括供应链方案设计与物流方案设计服务2个业务类型。</span><br><br><span>　　主要应用领域：重点应用于制造业，批发和零售业，交通运输、仓储和邮政业，电力、热力、燃气及水生产和供应业，公共管理、社会保障和社会组织等5个国民经济行业。</span><br><br><span>　　二十一、信息技术咨询服务</span><br><br><span>　　定义和范围：信息技术咨询服务是指为用户提供信息技术相关的咨询服务，包括信息技术的咨询服务、规划咨询服务、信息安全管理的咨询服务、保障企业数据安全与规范、外包发展路线设计、外包环节及流程规划、外包商选择、外包流程监管及交付物验收服务等。信息技术咨询服务属于信息技术外包（ITO）。</span><br><br><span>　　主要业务类型：主要包括信息技术战略咨询、信息安全咨询、外包策略咨询、设备软件分析优化咨询等4种业务。</span><br><br><span>　　主要应用领域：重点应用于包括信息传输、软件和信息技术服务业，制造业，电力、热力、燃气及水生产和供应业，批发和零售业等4个国民经济行业。</span><br><br><span>　　二十二、管理咨询服务</span><br><br><span>　　定义和范围：管理咨询服务是指运用现代化的手段和科学方法，通过对企业的诊断、培训、方案规划、系统设计与辅导，从集团企业的管理到局部系统的建立，从战略层面的确立到行为方案的设计，对企业生产经营全过程实施动态分析，协助其建立现代管理系统，提出行动建议，并协助执行这些建议，以达到提高企业经济效益的一种业务活动，但不包括信息技术咨询、软件咨询等业务活动。管理咨询服务属于知识流程外包（KPO）。</span><br><br><span>　　主要业务类型：主要包括战略咨询服务、业务咨询服务和综合解决方案服务等3个业务类型。</span><br><br><span>　　主要应用领域：重点应用于制造业，公共管理、社会保障和社会组织，信息传输、软件和信息技术服务业等3个国民经济行业。</span><br><br><span>　　二十三、人力资源管理服务</span><br><br><span>　　定义和范围：人力资源管理服务是指承接客户方内部一项或几项人力资源管理工作或职能，通常是长期的持续性服务，且通常是一个或若干个业务流程而不包括具体的单一业务活动，即不包括劳务派遣服务。人力资源管理服务属于业务流程外包（BPO）。</span><br><br><span>　　主要业务类型：主要包括招聘流程服务、薪酬服务、福利管理服务、外籍员工服务等4个业务类型。</span><br><br><span>　　主要应用领域：重点应用在金融业，制造业，信息传输、软件和信息技术服务业，批发和零售业，交通运输、仓储和邮政业，租赁和商务服务业等6个国民经济行业。</span><br><br><span>　　二十四、呼叫中心及电商后台服务</span><br><br><span>　　定义和范围：呼叫中心及电商后台服务是指将包括通信、金融、先进制造业、互联网等产业的语音、在线等客户服务业务，以外包的形式交付给市场化的企业运营管理，与发包方一起对用户服务资源统一管理和调度，构成一个包括服务、营销、咨询等一体化的语音、在线等全媒体服务形式，向用户提供全方位的按需服务。呼叫中心及电商后台服务属于业务流程外包（BPO）。</span><br><br><span>　　主要业务类型：主要包括语音服务、在线服务、智能客服等3个业务类型。</span><br><br><span>　　主要应用领域：重点应用在信息传输、软件和信息技术服务业，制造业，金融业，批发和零售业，交通运输、仓储和邮政业等5个国民经济行业。</span></p>','政策法规',20,'1551925499'),
	(10,'关于印发《南昌高新区降低企业用工成本的若干政策（试行）》的通知','','<p><span>机关各部门，各镇（处）、五星垦殖场，区属各单位，区内各企业：</span><br><br><span>&nbsp; &nbsp;《南昌高新区降低企业用工成本的若干政策（试行）》已经2017年第13次主任办公会审议通过，现印发给你们，请照此执行。</span><br><br><br><br><br><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;南昌高新区管委会办公室</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2017年8月15日</span><br><br><span>&nbsp;</span><br><br><span>&nbsp;</span><br><br><span>按照降成本优环境促发展专项行动要求，为切实降低企业用工成本，进一步优化发展环境，为企业发展提供充足的人力资源保障，特制定本政策。</span><br><br><span>一、降低企业社保成本</span><br><br><span>1.按国家、省、市规定阶段性降低企业职工养老保险、失业保险、工伤保险、生育保险缴费比例。</span><br><br><span>2.就业困难人员社保补贴。对企业招用就业困难人员、与之签订1年以上劳动合同并缴纳社会保险费的，按其为就业困难人员实际缴纳的基本养老保险费、基本医疗保险费和失业保险费给予企业补贴。补贴期限最长不超过3年（距法定退休年龄不足5年的可延长至退休）。&nbsp;</span><br><br><span>3.重点产业社保补贴。区内电子信息、生物医药、航空制造、新材料及高端智能装备产业规模以上企业新招用大中专院校毕业生或高级工以上技能人才、与之签订1年以上劳动合同并缴纳社会保险费的，给予企业社保补贴。补贴标准为每人300元/月，补贴期限累计最长不超过2年。</span><br><br><span>4.小微企业社保补贴。对招用毕业年度高校毕业生，与之签订1年以上劳动合同并为其缴纳社会保险费的小微企业和家庭服务业企业，给予最长1年的社会保险补贴。</span><br><br><span>二、降低企业培训成本</span><br><br><span>5.新招员工岗前培训补贴。企业组织与本企业签订6个月以上劳动合同的新招员工进行岗前培训，达到培训课时的，给予企业300元/人的培训补贴。</span><br><br><span>6.职业培训补贴。企业组织员工参加技能培训或新型学徒制培训，培训后取得职业资格证书的，根据培训工种、技术等级、培训时间，给予企业600-3000元/人的培训补贴。每人每年可享受一次职业培训补贴。员工初次参加职业技能鉴定并取得职业资格证书的，按鉴定收费标准的70%给予职业技能鉴定补贴，但最低不少于220元/人。</span><br><br><span>7.技能提升补贴。企业员工依法参加失业保险3年以上，累计缴纳失业保险费36个月以上的，自2017年1月1日起取得初级（五级）、中级（四级）、高级（三级）职业资格证书和职业技能等级证书的，分别给予员工1000元、1500元、2000元的技能提升补贴。</span><br><br><span>8.校企合作补贴。在与高新区主导产业相匹配的职业院校建设一批高新区技能人才培养输送基地。鼓励区内电子信息、生物医药、航空制造、新材料及高端智能装备产业规模以上企业与院校开展校企合作，通过“定向班”、“冠名班”等订单定向培养方式培养技能人才，给予企业5万元/班的补贴。</span><br><br><span>9.实习补贴。鼓励职业院校组织学生到区内规模以上企业开展教学实习、顶岗实习，实习时间在2个月以上的，给予院校500元/人的实习补贴。通过第三方机构组织学生实习的，经院校和企业确认，补贴可支付给第三方机构。</span><br><br><span>三、降低企业招聘成本</span><br><br><span>10.定期举办公益性招聘活动。每年举办大型综合型企业招聘会3场以上，每月举办2场企业专场招聘会，全年20场以上，区内企业免费参加。</span><br><br><span>11.开展跨区域劳务用工协作。在劳动力富余地区与当地人社部门及人力资源市场建立劳务用工对接协作机制，成立招工小分队协助企业招工，引导外省市劳动力到区内企业就业。</span><br><br><span>12.免费发布企业招聘信息。通过政府购买服务的方式，在国内知名招聘网站开辟南昌高新区重点企业招聘专栏，免费为区内企业刊登招聘信息。</span><br><br><span>13.免费为新投产（扩产）重点工业企业招工。对经招商局（经发局）认定的新投产（扩产）重点工业企业，通过政府购买服务的方式，选定综合实力强的人力资源公司为企业免费输送投产（扩产）所需员工。</span><br><br><span>14.招工补贴。区内电子信息、生物医药、航空制造、新材料及高端智能装备产业规模以上企业自行组织招聘员工，并与员工签订1年以上劳动合同、缴纳社会保险且在企业工作3个月以上的，给予企业招工补贴200元/人。</span><br><br><span>15.中介招工奖。鼓励人力资源中介机构为企业输送员工，事前向区人社局报备后，为区内规模以上企业引进外来务工人员，并签订1年以上劳动合同、缴纳社会保险且在企业工作3个月以上的，给予中介机构400元/人奖励。纳入市级购买用工服务的企业和中标人力资源服务机构，按照市政府《关于进一步做好工业园区企业用工服务工作意见》（洪府发〔2015〕93号）文件精神给予就业补贴。对重大项目紧缺员工可采取一事一议的方式给予支持。</span><br><br><span>16.员工推荐奖。鼓励企业推广“以工招工”模式，区内规模以上企业员工通过“亲带亲”、“邻带邻”、“老乡带老乡”等方式向企业推荐员工就业，员工事前向用工单位报告，再由用工单位汇总向区人社局报备后，介绍首次来高新区务工人员到员工所在企业就业并签订1年以上劳动合同、缴纳社会保险且在企业工作3个月以上的，给予推荐人200元/人奖励。</span><br><br><span>17.院校推荐奖。职业院校为区内规模以上企业培养输送毕业生，并签订1年以上劳动合同、缴纳社会保险且在企业工作3个月以上的，给予推荐院校2000元/人奖励。通过第三方机构推荐职业院校毕业生到企业就业的，经企业确认，补贴可支付给第三方机构。</span><br><br><span>18.猎头引才资助。区内规模以上企业通过猎头公司引进中层以上的技术和管理岗位，年收入20万元以上，补贴猎头服务费用的50%，上限为6万元/职位。每个企业每年获猎头补贴上限为20万元。</span><br><br><span>19.淡旺季用工调剂。引导企业结成用工联盟，实现用工信息共享，错峰借人，鼓励生产旺季的企业从生产淡季企业借用员工，盘活劳动力资源。</span><br><br><span>四、降低企业稳工成本</span><br><br><span>20.稳岗补贴。对依法参保、上年度未裁员或裁员率低于统筹地区城镇登记失业率的企业，给予企业上年度实际缴纳失业保险金额50%的稳岗补贴。稳岗补贴主要用于企业职工生活补助、缴纳社会保险费、转岗培训、技能提升培训等相关支出。</span><br><br><span>21.生活补贴。对经管委会认定的电子信息、生物医药、航空制造、汽车四大产业重点企业新引进的紧缺专业高校毕业生按照《关于扶持南昌高新区战略性产业人才引进的具体措施》（洪高新管字〔2016〕517号文件给予安家补助或房租补助。</span><br><br><span>五、其他</span><br><br><span>22.区内企业员工离职后一年内到区内同类型企业工作的，不再享受本政策的奖励补贴。</span><br><br><span>23.以上奖励补贴若与国家、省、市、南昌高新区其他扶持政策相复时，按就高不就低的原则执行。</span><br><br><span>24.本政策所支持的企业是指工商、税务、统计关系均在南昌高新区内的企业。</span><br><br><span>25.由区人力资源和社会保障局根据本政策制订补贴奖励申报细则。</span><br><br><span>26.本政策自2017年6月1日起施行，由区人力资源和社会保障局负责解释。2015年4月22日印发的《关于加强企业用工服务稳定员工队伍的若干意见（试行）》（洪高新管办发〔2015〕6号）同时废止。</span></p>','政策法规',31,'1551925721'),
	(11,'人力资源社会保障部关于印发人力资源服务业发展行动计划的通知','','<p><span>人社部发〔2017〕74号</span><br><span>&nbsp;</span><br><br><span>各省、自治区、直辖市及新疆生产建设兵团人力资源社会保障厅（局），外交部、国家发展改革委、教育部、工业和信息化部、财政部、商务部、国家税务总局、国家工商行政管理总局办公厅：</span><br><br><span>为贯彻落实国务院《“十三五”促进就业规划》和《关于做好当前和今后一段时期就业创业工作的意见》关于实施人力资源服务业发展行动计划的有关要求，我们研究制定了《人力资源服务业发展行动计划》，现印发给你们，请各地和有关部门高度重视，结合实际情况，制定具体实施方案，抓好组织落实，确保各项任务顺利完成。</span><br><br><span>&nbsp;</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 人力资源社会保障部</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2017年9月29日</span><br><br><span>&nbsp;</span><br><br><span>&nbsp;</span><br><br><span>人力资源服务业发展行动计划</span><br><br><span>&nbsp;</span><br><br><span>人力资源是推动经济社会发展的第一资源。人力资源服务业是生产性服务业和现代服务业的重要组成部分，对推动经济发展、促进就业创业和优化人才配置具有重要作用。近年来，人力资源服务业快速发展，新模式、新业态不断涌现，服务产品日益丰富，服务能力进一步提升，但与我国经济社会发展对人力资源服务业的要求相比，与世界先进水平相比，还有一定差距。为贯彻落实国务院《“十三五”促进就业规划》、国务院《关于做好当前和今后一段时期就业创业工作的意见》关于实施人力资源服务业发展行动计划的有关要求，制定本行动计划。</span><br><br><span>一、总体要求</span><br><br><span>（一）指导思想</span><br><br><span>全面贯彻党的十八大和十八届三中、四中、五中、六中全会精神，深入贯彻习近平总书记系列重要讲话精神和治国理政新理念新思想新战略，围绕贯彻实施就业优先战略和人才强国战略，充分发挥市场在人力资源配置中的决定性作用和更好发挥政府作用，以产业引导、政策扶持和环境营造为重点，健全管理制度，完善服务体系，提高服务质量，推动人力资源服务业快速发展，为实现充分就业和优化配置人力资源，促进经济社会发展，提供优质高效的人力资源服务保障。</span><br><br><span>（二）基本原则&nbsp;</span><br><br><span>市场主导，政府推动。发挥市场在人力资源配置中的决定性作用，推进统一规范的人力资源市场建设，鼓励各类人力资源服务机构适应市场需求，提供优质高效的人力资源服务。更好发挥政府作用，完善人力资源市场法规制度体系，加强人力资源市场监管，处理好政府与市场的关系，促进公共服务和经营性服务协同发展。</span><br><br><span>融合创新，集聚发展。鼓励人力资源服务企业向价值链高端发展，推动跨界融合，探索新兴业态，开发服务产品，拓展服务内容，创新服务方式，提升人力资源服务供给水平。发挥人力资源服务业的集聚效应，建设人力资源公共服务枢纽型基地和产业创新发展平台。</span><br><br><span>促进交流，开放合作。坚持国际国内两个市场协调发展，落实国家构建开放型经济新体制的要求，营造良好投资环境，积极引进国际知名人力资源服务品牌。加大国际交流与合作力度，鼓励国内人力资源服务机构走出去，积极开拓国际业务。</span><br><br><span>（三）目标任务：到2020年，基本建立专业化、信息化、产业化、国际化的人力资源服务体系，实现公共服务有效保障、经营性服务逐步壮大，服务就业创业与人力资源开发配置能力显著提高，人力资源服务业对经济增长贡献率稳步提升。</span><br><br><span>产业规模进一步增长。到2020年，产业规模达到2万亿元。培育形成100家左右在全国具有示范引领作用的行业领军企业。培育一批有特色、有规模、有活力、有效益的人力资源服务产业园。</span><br><br><span>行业结构进一步优化。人力资源服务行业结构更加合理，服务主体进一步多元化，服务业态更加丰富，产品附加值显著提高，各类业态协调发展。</span><br><br><span>人才队伍进一步壮大。不断提高从业人员专业化、职业化水平。培养行业领军人才，加大行业高层次人才培养和引进力度。到2020年行业从业人员达到60万，领军人才达到1万名左右。</span><br><br><span>服务能力进一步提升。人力资源服务市场化、专业化水平和国际竞争力大幅提升，服务方式不断改进，服务质量明显提升，规模化、品牌化和信息化水平不断提高，为“一带一路”建设、“京津冀协同发展”等国家重大战略提供人力资源支撑保障能力显著增强。</span><br><br><span>二、主要内容</span><br><br><span>实施骨干企业培育计划、领军人才培养计划、产业园区建设计划和“互联网+”人力资源服务行动、诚信主题创建行动、“一带一路”人力资源服务行动，进一步改善发展环境，培育市场主体，推进业态创新，加快开放合作。</span><br><br><span>（一）骨干企业培育计划</span><br><br><span>坚持政府引导、市场运作、科学规划、合理布局，支持通过兼并、收购、重组、联盟等方式，重点培育一批有核心产品、成长性好、具有国际竞争力的综合性人力资源服务骨干企业。加快发展有市场、有特色、有潜力的专业化人力资源服务骨干企业，引导人力资源服务企业细化专业分工，向价值链高端延伸。鼓励开展高级人才寻访、人力资源管理咨询、人力资源服务外包等服务的企业，按照产业结构转型升级的要求加快发展。鼓励企业注册和使用自主人力资源服务商标，开展自主品牌建设，形成一批知名企业和著名品牌。支持人力资源服务企业参选本地区服务业重点企业名录和高新技术企业名录。发挥国有控股人力资源服务企业示范引导作用。鼓励有条件的地方通过设立人力资源服务业发展基金等多种方式，扶持人力资源服务骨干企业发展。</span><br><br><span>（二）领军人才培养计划</span><br><br><span>着力提高从业人员专业化、职业化水平，建设一支素质优良、结构合理的人力资源服务业人才队伍。加大人力资源服务业高层次人才培养力度，依托著名高校、职业院校、大型企业、跨国公司，建立人力资源服务培训基地和实训基地，开展行业高层次人才培训。实施人力资源服务业领军人才研修计划，举办高研班，开展学术交流和研修活动。加大高层次人才引进力度，将其纳入国家和地方有关人才计划和人才引进项目，享受相关优惠政策。建立行业领军人才库，及时准确全面地掌握行业领军人才情况。加强人力资源服务业理论研究，鼓励有条件的高校培养人力资源服务业方向的专业硕士，加大专业学术人才培养力度。</span><br><br><span>（三）产业园区建设计划</span><br><br><span>加强顶层设计和统筹规划，以国家中心城市、区域中心城市以及国家级新区、高新技术开发区、经济技术开发区、边境经济合作区、跨界经济合作区等产业聚集区域为依托，继续抓紧培育建设一批有规模、有辐射力、有影响力的国家级人力资源服务产业园。鼓励有条件的地区根据本地经济发展和产业转型需要，培育建设一批有特色、有活力、有效益的地方产业园。探索园区管理模式创新，提升园区建设水平，充分发挥园区集聚发展和辐射带动作用。加强园区管理，落实产业园在税收、资金、工商、土地用房等方面的政策，充分发挥园区培育、孵化、展示、交易功能。开展人力资源服务产业园建设评估，对已经建成运行的产业园进行考核评估，总结园区平台建设、管理服务、机制创新、服务创新等方面的成效与经验。</span><br><br><span>（四）“互联网+”人力资源服务行动</span><br><br><span>落实国家“互联网+”发展战略要求，推动人力资源服务和互联网的深度融合，积极运用大数据、云计算、移动互联网、人工智能等新技术，促进人力资源服务业创新发展、融合发展。加强人力资源服务信息化建设，构建人力资源信息库，实现数据互联互通，信息共享。鼓励人力资源服务企业设立研发机构，加强服务手段、商业模式、关键技术的研发和推广应用。鼓励人力资源服务企业与互联网企业开展技术合作，支持互联网企业跨界兼营人力资源服务业务。进一步加强行业交流，鼓励地方搭建人力资源服务业创新发展平台，举办人力资源服务供需对接、服务产品推介等活动。支持人力资源服务企业运用互联网技术探索开展与金融、教育、医疗等行业的跨界服务模式。推进人力资源服务业信息化基础设施建设，健全信息安全保护制度。</span><br><br><span>（五）诚信主题创建行动。持续推动人力资源服务行业诚信体系建设，围绕诚信服务树品牌、规范管理促发展，深入开展人力资源服务机构诚信主题创建行动，打造一批“全国人力资源诚信服务示范机构”，发挥先进典型在市场管理和行业发展中的引领带动作用。鼓励各地结合实际开展选树人力资源诚信服务示范机构活动。围绕推进诚信体系建设，加快构建守信激励和失信惩戒的良好制度环境，建立健全人力资源服务机构信用记录，纳入全国信用信息共享平台，形成诚实守信、规范发展的共同理念和良好氛围，不断提升诚信服务、优质服务水平。</span><br><br><span>（六）“一带一路”人力资源服务行动</span><br><br><span>围绕服务“一带一路”建设战略，积极构建公平稳定、透明高效、监管有力、接轨国际的人力资源服务业外商投资管理体制。稳步推进人力资源市场对外开放，引进我国市场急需的海外人力资源服务企业，加强与国际知名人力资源服务机构的合作。加强与“一带一路”沿线国家人力资源服务交流合作，鼓励吸引“一带一路”沿线国家人力资源服务业在我国投资设立人力资源服务企业。支持国内人力资源服务企业为“一带一路”沿线国家在我国设立的企业提供人力资源服务。根据对等开放原则，积极推动降低市场准入壁垒，鼓励有条件的人力资源服务企业在“一带一路”沿线国家设立分支机构，大力开拓国际市场，构建全球服务网络，为我国企业“走出去”承接国际服务，提供特色化、精细化人力资源服务。积极搭建“一带一路”人力资源服务国际交流合作平台，在“一带一路”国际合作高峰论坛框架下，推动举办人力资源服务业国际合作论坛。</span><br><br><span>三、组织实施</span><br><br><span>（一）加强组织领导。各地要加强组织领导，将发展人力资源服务业纳入本地经济社会发展规划和就业、人才、服务业发展规划，统筹谋划人力资源服务业发展。健全工作机制，加强与组织、发改、财政、工商、税务等部门的协调，协同配合，形成合力，及时研究解决人力资源服务业发展中的重大问题。加强对人力资源服务业发展的考核评价，确保落实促进人力资源服务业发展的各项措施。</span><br><br><span>（二）加大支持力度。各地要结合实际，制定人力资源服务业行动计划实施方案，出台有针对性的政策措施。落实人力资源服务业发展的税收相关政策。鼓励和引导经营性人力资源服务机构参与社会公益服务。稳步推进政府向社会力量购买人力资源服务，研究推动将人力资源服务纳入政府购买服务的指导目录，明确政府购买人力资源服务种类、性质和内容。有条件的地方要投入专项资金，对重点领域和薄弱环节给予支持。推动人力资源服务企业积极进入资本市场。进一步放宽人力资源服务业市场准入，鼓励社会资本进入人力资源服务领域。</span><br><br><span>（三）营造良好发展环境。贯彻落实国务院“放管服”改革要求，依法规范实施人力资源服务行政许可，进一步简化优化审批流程，提高服务的便捷性和可及性。加强人力资源市场事中事后监管，实施随机抽查，建立年度报告公示制度。强化劳动保障监察执法，持续开展清理整顿人力资源市场秩序专项行动，加大对网络招聘平台的监管力度，严肃查处违法违规行为。加强人力资源服务标准化建设，建立健全标准体系。加强人力资源服务机构能力建设，探索建立人力资源服务机构等级评价体系。建立人力资源市场供求预测和信息发布制度，编撰人力资源服务业发展报告。实施西部和东北地区人力资源市场建设援助计划。培育发展各类人力资源服务行业协会，充分发挥行业协会作用。</span><br><br><span>（四）加强宣传引导。各地要加强对人力资源服务业的宣传，充分发挥各级人力资源社会保障网站第一平台作用，积极探索利用政务微博、微信和手机APP等新媒体手段，不断提高宣传的有效性和影响力。鼓励各地和行业协会举办人力资源服务业发展博览会、人力资源服务业创新创业大赛、人力资源服务发展论坛等活动。丰富宣传内容，创新宣传形式，注意总结人力资源服务业在促进就业创业、优化人才开发和服务经济发展等方面发挥的重要作用，扩大人力资源服务业的知名度、美誉度和社会影响力。</span></p>','政策法规',25,'1551925751'),
	(12,'南昌市服务外包产业发展专项资金使用管理办法','','<p><span>第一章　 总则</span><br><br><span>第一条　 根据《南昌市人民政府关于进一步加快服务外包产业发展的实施意见》（洪府发〔2015〕22号）的精神，为规范南昌市服务外包产业发展专项资金（以下简称“专项资金”）的使用管理，充分发挥专项资金的引导和激励作用，特制定本办法。</span><br><br><span>第二条　 服务外包是指：专业服务供应商通过契约（合同、协议）的方式，为组织（企业、政府、社团等）提供服务，借助信息技术完成组织内部现有或新增的业务流程的经济活动。</span><br><br><span>第三条　 专项资金使用范围：重点支持离岸（在岸）服务外包业务、国际资质认证、人才培训、园区建设、呼叫坐席、公共服务等方面。</span><br><br><span>第二章　 申请条件</span><br><br><span>第四条　 申请专项资金的服务外包企业应同时具备以下条件：</span><br><br><span>一、在南昌市辖区内工商注册、税务登记，具有企业法人资格并合法承接服务外包业务的企业；</span><br><br><span>二、在商务部“服务外包业务管理和统计系统”中注册，填报服务外包业务数据并通过审核，且通过审核的数据应符合以下条件之一：</span><br><br><span>1.年离岸接包合同执行金额不低于20万美元；</span><br><br><span>2.年在岸接包合同执行金额不低于300万元人民币；</span><br><br><span>3.年接包合同执行总金额不低于300万元人民币。</span><br><br><span>第五条　 申请专项资金的服务外包人才重点培训机构应同时具备以下条件：</span><br><br><span>一、在南昌市辖区内经相关部门批准设立、具有服务外包类人才培训从业资格并依法开展运营的培训机构；</span><br><br><span>二、在商务部“服务外包业务管理和统计系统”中注册；</span><br><br><span>三、经市服务外包业务主管部门认定为“南昌市服务外包人才重点培训机构”。</span><br><br><span>第三章　 支持标准</span><br><br><span>第六条　 对当年离岸服务外包业务额不低于20万美元的企业，按1美元离岸业务奖励0.2元人民币的标准给予奖励。每家企业每年奖励最高不超过100万元。</span><br><br><span>第七条　 对当年在岸服务外包业务额不低于300万元人民币的企业，按1元人民币在岸业务奖励0.01元人民币的标准给予奖励。每家企业每年奖励最高不超过50万元。</span><br><br><span>第八条　 对当年服务外包业务总额折合人民币不低于300万元的企业，初次通过相关国际资质认证给予奖励。其中，通过CMMI3（CMM3）认证，奖励15万元；通过CMMI4（CMM4）认证，奖励25万元；通过CMMI5（CMM5）认证，奖励40万元；通过人力资源成熟度模型（PCMM）、信息安全管理（ISO27001/BS7799）、IT服务管理（ISO20000）、服务提供商环境安全性（SAS70）、国际实验动物评估和认可委员会认证（AAALAC）、优良实验室规范（GLP）、信息技术基础架构库认证（ITIL）、客户服务中心认证（COPC）、环球同业银行金融电讯协会认证（SWIFT）、质量管理体系要求（ISO9001）、业务持续性管理标准（BS25999）等相关认证，每个认证奖励10万元。</span><br><br><span>第九条　 对经我市重点培训机构培训三个月以上的大专学历以上（含大专）的学员，与我市服务外包企业签订1年以上（含1年）劳动合同且交纳社会保险的，按4500元/人的标准给予培训机构补助。先就业后培训的参照执行。</span><br><br><span>第十条　 对当年新建不低于200个座席的外包型呼叫类企业，按每个座席1000元的标准给予补助。后续年度新增坐席，参照上述标准给予补助。</span><br><br><span>第十一条　 对当年获得省级服务外包示范园区称号的园区，给予10万元的奖励；对当年获得省级优秀服务外包示范园区称号的园区，给予10万元的奖励。</span><br><br><span>第十二条　 专项资金项目管理费，由市财政按不超过专项资金规模的3%核拨，专项用于项目管理、评审、调研及相关业务培训等。</span><br><br><span>第四章　 申报程序</span><br><br><span>第十三条　 专项资金的申请、审核和拨付，遵循以下程序：</span><br><br><span>一、市商务局、市财政局下发申报专项资金的通知；</span><br><br><span>二、符合条件的申报主体，向所在县（区）、开发区（新区）服务外包业务主管部门递交申报材料；</span><br><br><span>三、县（区）、开发区（新区）服务外包业务主管部门会同同级财政部门进行审核，形成审核记录并上报市商务局；</span><br><br><span>四、市商务局收到申报材料后，会同市财政局进行审核，形成审核记录和意见上报市政府批准；</span><br><br><span>五、市财政局根据市政府批复意见，下拨资金。</span><br><br><span>第五章　 附则</span><br><br><span>第十四条　 获得专项资金支持的单位，应严格按照相关规定管理和使用资金，并自觉接受监督检查。</span><br><br><span>第十五条　 本办法如与市本级其他政策相重复时，可按就高不就低的原则执行。</span><br><br><span>第十六条　 本办法自印发之日起执行，由市商务局和市财政局负责解释。原《南昌市人民政府关于印发南昌市进一步促进服务外包产业发展的扶持政策的通知》（洪府发〔2013〕36号）和《南昌市财政局 南昌市对外经济贸易合作委员会关于印发南昌市进一步促进服务外包产业发展专项资金管理办法的通知》（洪财企〔2013〕77号）同时废止。</span><br><br><br><span>附件：</span><br><br><span>服务外包产业发展专项资金申报材料</span><br><br><span>&nbsp;</span><br><br><span>一、共性材料</span><br><br><span>1.申请报告；</span><br><br><span>2.营业执照副本或培训从业资质复印件；</span><br><br><span>3.具有从业资质的中介机构出具的、含有申报内容的专项审计报告（申请第十一条不需提供）。</span><br><br><span>二、个性材料</span><br><br><span>（一）申请第六条的企业，应当提交以下申报材料：</span><br><br><span>1.企业与境外客户签订的离岸服务外包业务合同或协议复印件；</span><br><br><span>2.与合同或协议相对应的“结汇转账贷方凭证”和“涉外收入申报单”复印件。</span><br><br><span>（二）申请第七条的企业，应当提交以下申报材料：</span><br><br><span>1.企业与境内客户签订的在岸服务外包业务合同或协议复印件；</span><br><br><span>2.与合同或协议相对应的“银行到帐凭证”复印件。</span><br><br><span>（三）申请第八条的企业，应当提交以下申报材料（如同时申报第六条折合人民币超过300万元或申报第七条的只需提供材料3）：</span><br><br><span>1.企业与客户签订的服务外包业务合同或协议复印件；</span><br><br><span>2.与合同或协议对应的银行到账凭证复印件（离岸业务提供“结汇转账贷方凭证”和“涉外收入申报单”；</span><br><br><span>3.国际资质认证证书复印件。</span><br><br><span>（四）申请第九条的培训机构，应当提交以下申报材料：</span><br><br><span>1.学员大专以上（含大专）学历证明及身份证复印件；</span><br><br><span>2.培训收费凭证（正式发票）和培训课程安排复印件；</span><br><br><span>3.培训机构颁发的学员专业考核合格证书复印件；</span><br><br><span>4.学员与服务外包企业签订的1年以上（含1年）劳动合同复印件；</span><br><br><span>5.学员被录用企业为其缴纳申报年度社保证明。</span><br><br><span>（五）申请第十条的企业，应当提交以下申报材料：</span><br><br><span>搭建坐席的相关软硬件设备（设施）的购置发票及付款凭证复印件。</span><br><br><span>（六）申请第十一条的园区，应当提交以下申报材料：</span><br><br><span>1.“省级服务外包示范园区”或“优秀省级服务外包示范园区”认定文件复印件；</span><br><br><span>2.园区产业发展报告。</span><br></p>','政策法规',36,'1551925775'),
	(13,'两会快递 | 保障老有所养，如何建立多层次社保体系？','','<p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>今年的政府工作报告中10次提到“养老”。养老金实现14连涨，更是让大家感到满满的幸福。而2005年以来，全国人均退休金从每月714元提高到2016年的2373元，增长了232%。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　目前，我国社会养老保险覆盖9亿多人，基本医疗保险覆盖13.5亿人，织就了世界上最大的社会保障网。然而，人们对“老有所养”的保障仍有更高期待，比如：</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　养老金能不能再高点?</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　养老保险能不能跟人走?</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　转移接续可不可以再顺畅一点?</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　对于提高养老金待遇的问题，两会期间国家发改委提交的报告提出，要推动建立多层次社会保障体系，建立基本养老保险、企业职业年金和商业养老保险三大支柱。眼下，第一支柱日益完善;第二支柱正在提速。今年2月1日正式实施的《企业年金办法》，明确企业和职工可自主建立企业年金。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　人社部社会保障研究所所长金维刚说，企业年金将成为我国养老保险体系的重要补充。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　除了从国家那领取基本的养老金之外，还有一部分是来源于企业建立的补充养老保险，这样可以有利于提高待遇水平。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　同时，人社部、财政部等部门，鼓励发展个人储蓄性养老保险和商业养老保险。人社部新闻发言人卢爱红说：</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　将按照全面建成多层次社会保障体系的要求，研究提出第三支柱制度建设的意见和建议。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　除此之外，划转部分国有资本充实社保基金的工作，也在推进。全国人大代表、国务院国资委主任肖亚庆说，今年将选择三家央企试点。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　划转并不影响中央企业的运转，不但不影响全资的中央企业运转，也不影响股权多元化，甚至混合所有制的企业的运转。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　而要实现“养老保险跟着人走”，目前最大的障碍就是养老制度地方分割统筹管理。不过，今年的政府工作报告提出“深化养老保险制度改革，建立企业职工基本养老保险基金中央调剂制度”。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　全国人大代表、中国人民大学教授郑功成说：设立中央调剂金，就是从各个省征集的社会养老保险费中提取一部分由中央来支配，加上中央财政对地方养老保险的转移支付，可以在全国各省市之间统一调剂使用。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　这样实际上是向全国统筹十分关键的一步。基本养老保险制度实现了全国统筹，只要是在中华人民共和国境内，不管在哪个地方工作，不管在哪个地方自己交的费还是用人单位交的费，都会来到全国统一的账户之中。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　不过，老有所养绝不能光盯着养老金，还要提高养老服务。眼下，各地大规模建起养老院、老年公寓，而很多愿意居家养老的老年人，却无法获得相应的养老服务。郑功成建议，应把养老机构开到社区里，建立包括兼具老年公寓、托老所、老人寄养中心等功能的”综合型养老服务中心“，同时提供家政、餐饮、医疗等服务。而政府应该用多种优惠政策，撬动民间资本投入养老服务产业。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　民政部部长黄树贤说，针对这些需求，今年将推进“智慧养老”“医养结合”试点。</span></p><p background-color:#ffffff;\"=\"\" style=\"text-align: justify;\"><span>　　民政部将推动中国特色养老服务业加快发展，深化养老服务业“放管服”的改革，推动取消养老机构的设立许可，完善四种事后监管的机制。深化居家和社区养老服务改革，制定互联网加养老政策措施，推进“智慧养老”“医养结合”等四点工作。</span></p>','政策法规',11,'1551925798'),
	(14,'买房须知：住房公积金使用新政策通知','','<p><span>公积金贷款的利率比较低，可以省下很多贷款成本，不过办理流程比较复杂，条件比较严格，这使得部分人放弃了办理的想法。针对这种情况，国家进行了相应的调整，具体情况一起来看看吧。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 为切实提高住房公积金使用效率，按照国务院关于加快落实住房公积金使用政策的督查要求，现就有关事项通知如下：</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;一、提高实际贷款额度</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 2015年8月末住房公积金资金运用率低于85%的设区城市，要综合考虑当地房价水平、贷款需求和借款人还款能力，提高住房公积金个人住房贷款实际额度。在保证借款人基本生活费用的前提下，月还款额与月收入比上限控制在50%-60%。贷款偿还期限可延至借款人法定退休年龄后5年，最长贷款期限为30年。推行按月划转住房公积金冲还贷款本息业务。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;二、设区城市统筹使用资金</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;同一设区城市住房公积金管理中心和分中心应当统一住房公积金提取和贷款政策，统筹使用贷款资金。住房公积金管理中心或分中心贷款资金不足时，应允许缴存职工向同城住房公积金管理机构申请贷款。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;三、拓宽贷款资金筹集渠道</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 有条件的城市要积极推行住房公积金个人住房贷款资产证券化业务，盘活住房公积金贷款资产。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;四、全面推行异地贷款业务</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;缴存职工在缴存地以外地区购房，可按购房地住房公积金个人住房贷款政策向购房地住房公积金管理中心申请个人住房贷款。缴存地和购房地住房公积金管理中心应相互配合，及时出具、确认缴存证明等材料，办理贷款手续。具体办法由住房城乡建设部另行制定。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 五、简化业务审批要件</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 缴存职工申请住房公积金个人住房贷款、同意根据本人住房公积金月缴存额推算其月收入的，不需单位出具职工收入证明。缴存职工租住商品住房申请提取住房公积金，除身份证明、本人及配偶无房证明外，不需提供其它证明材料。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 六、提高管理效率和服务水平</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 各地住房公积金管理中心要优化内部人员配置，增加网点工作人员，工资待遇向网点工作人员倾斜。要充分利用受托银行业务网点优势，方便缴存职工就近办理住房公积金提取和贷款手续。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 七、加快改造升级信息系统</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 各地住房公积金管理中心要根据政策调整和流程优化的需要，加快改造升级住房公积金管理信息系统，建立集12329服务热线、短信、微信、手机APP、网上业务大厅等功能于一体的综合服务平台，推进办理网上业务，为缴存职工提供高效便捷的服务。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 八、建立考核问责制度</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 各级住房公积金监管部门要加强对城市住房公积金管理中心业务考核，将住房公积金资金运用率或住房公积金个人住房贷款市场占有率作为重要考核指标，考核结果要通报设区城市人民政府，并作为考核住房公积金管理中心负责人的重要参考。住房公积金资金运用率或住房公积金个人住房贷款市场占有率低的城市，要对住房公积金管理中心主要负责人进行约谈和问责。</span></p>','政策法规',44,'1551925823'),
	(15,'社保一卡通全国通用','','<span>社保问题与每个人都有关,但很多人在生活工作中或许会碰到一些难题,比如异地社保转续比较麻烦、异地就医需要各种盖章等。下面小编为大家分享明年基本实现全国社保一卡通，以下是详细资料：</span><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 人力资源和社会保障部近日出台了加强和改进人力资源社会保障领域公共服务的意见，以避免“奇葩证明”，提升服务质量。意见提出，各级人力资源社会保障部门要对办理公共服务事项所需证明材料和手续进行全面清理，凡没有法律法规或规章依据的证明和盖章环节原则上予以取消。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;意见提出，要重点加强异地业务系统建设，提升社会保险关系转移接续、异地就医结算、异地领取待遇资格认证等异地业务的经办效率，方便参保人就近办事，避免“垫资”“跑腿”等情况出现。通过“互联网+政务”，让“奇葩证明”和办事难等问题得到破解。为了方便百姓办事，意见要求各地需要探索创新公共服务方式，包括在综合服务机构推行“一站式”服务，逐步将分设的专业窗口整合为综合窗口，变“多头受理”为“一口受理”。</span><br><br><span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;此外，意见还提出2017年实现社会保障卡跨地区、跨业务直接办理个人的各项人力资源和社会保障事务，开放向其他公共服务领域的集成应用，基本实现全国社会保障一卡通。</span><br><span>&nbsp; &nbsp; &nbsp; &nbsp; 今后，各级人社部门重点落实取消收取人事关系及档案保管费的规定，不得将参加社会保险、职称评审等业务与档案保管相挂钩，杜绝以档案为载体的捆绑收费、隐形收费行为。在省级行政区域内，对符合条件的失业人员及时办理失业登记，不得以人户分离、户籍不在本地或没有档案等为由不予受理。</span><br>','政策法规',22,'1551925864'),
	(16,'关于加快人力资源服务业发展的意见','','<p><span>江西省人民政府关于加快人力资源服务业发展的意见</span><br><span>赣府发〔2018〕27号</span><br><br><span>各市、县（区）人民政府，省政府各部门：</span><br><span>　　为贯彻落实《国务院关于印发“十三五”促进就业规划的通知》（国发〔2017〕10号）、《国务院关于做好当前和今后一段时期就业创业工作的意见》（国发〔2017〕28号）和《中共江西省委关于深化人才发展体制机制改革的实施意见》（赣发〔2017〕4号）中关于大力发展人力资源服务业的要求，切实激发人力资源市场活力，为我省经济社会发展培育新增长点、形成新动能，现结合我省实际，就加快人力资源服务业发展提出如下意见。</span><br><span>　　一、指导思想</span><br><span>　　以习近平新时代中国特色社会主义思想为指引，全面贯彻落实党的十九大精神，深入实施“创新引领、改革攻坚、开放提升、绿色崛起、担当实干、兴赣富民”工作方针，充分发挥市场在人力资源配置中的决定性作用和更好发挥政府作用，以规划引领、产业引导、政策扶持、环境营造为重点，通过建设人力资源服务产业园，推动人力资源服务业向专业化、信息化、产业化、国际化方向创新发展，拓宽服务范围，提高服务能力，打造人才引进新高地和经济发展新增长极，为助力我省经济社会发展，建设富裕美丽幸福现代化江西提供强有力的人力资源支撑。</span><br><span>　　二、基本原则</span><br><span>　　（一）注重政策引导与市场化发展相结合。通过加强产业规划引导、政策扶持和组织协调，大力培育发展各类人力资源服务机构，按照市场规律，进一步强化企业主体地位，充分发挥市场在人力资源配置中的决定性作用。</span><br><span>　　（二）注重产业集聚与内联外引统筹推进相结合。通过资源整合和人力资源服务产业园建设，引进、集聚国内外知名机构和高端人才，大幅提升人力资源服务业竞争力。</span><br><span>　　（三）注重产业创新融合与促进服务转型相结合。适应经济社会发展要求，加强人力资源服务业与其他产业之间的联动，为加快转变发展方式、促进我省产业转型升级提供人才支撑。</span><br><span>　　（四）注重行业规范与依法监管相结合。在法律法规框架内，规范行业标准和队伍建设，加强行业管理和行业自律，建立健全人力资源服务业市场管理体系，营造公平竞争、健康有序的良好发展环境。</span><br><span>　　三、目标任务</span><br><span>　　（一）人力资源服务产业综合实力大幅提升。力争到2020年，全省人力资源服务机构总数达到1300家，产业规模达到700亿元，培育行业龙头企业和知名机构30家，行业领军企业3-4家，争取5年内产生上市公司1-2家。</span><br><span>　　（二）人力资源服务产业集聚效应不断显现。力争到2020年，引进部分国（境）内外高端企业和国家级行业领军企业，将国家级中国南昌人力资源服务产业园打造成在中部地区乃至全国有集聚、引领、辐射效应的人力资源服务产业基地；建成省级人力资源服务产业园6-8个，逐步形成我省人力资源服务产业集群高地。</span><br><span>　　（三）人力资源服务专业化水平迅速提高。不断创新人力资源服务模式和服务业态，不断丰富服务项目，推动人力资源服务业向专业化分工、信息化共享、价值链高端迈进；专业人员队伍不断壮大，力争到2020年，从业人员总量超过2万人，高层次人才占比明显提高，行业领军人才达到300人左右。</span><br><span>　　（四）人力资源服务业市场配置效能显著增强。力争到2020年，全行业服务各类人员1200万人次，服务用人单位60万家次。人力资源服务业成为人力资源和人才市场化配置的主渠道。</span><br><span>　　（五）人力资源服务信息化水平和创新能力不断提升。通过实施“互联网+”行动，力争到2020年，人力资源行业运用云计算、大数据等新技术在提供科学决策、开展精准服务、创新服务能力方面作用显著提升。</span><br><span>　　（六）人力资源服务市场体系进一步规范。力争到2020年，基本建立比较完善的人力资源服务业政策法规体系、标准规范体系、诚信服务体系，市场竞争公平、健康、有序。</span><br><span>　　四、政策措施</span><br><span>　　（一）发展各类人力资源服务机构。</span><br><span>　　培育龙头企业。重点培育一批有核心服务产品、成长性好、具有国际竞争力的综合性人力资源服务骨干企业。对获得国家级行业领军企业荣誉称号的人力资源服务企业，并在江西服务期满3年以上，由省财政一次性奖励100万元。对获得江西省省级行业领军企业荣誉称号的人力资源服务企业，并在江西服务期满3年以上的，由所在县（市、区）政府给予一次性奖励20万元。</span><br><span>　　引进知名企业。坚持高端引领，重点引进一批信誉度好、专业化程度高、高端服务能力强的国（境）内外及国家级行业领军企业，鼓励其与本地人力资源服务企业通过合资、收购、参股、联营等多种方式开展合作。对在我省注册新引进和新设立的高端人力资源服务企业根据其行业综合排名、发展前景和发展规模等因素，各地政府可给予一定的补贴和奖励。鼓励人力资源服务企业开展产业招商，对引进国（境）内外高端企业或国家级行业领军企业由省财政和所引进企业注册地政府分别给予一定奖励。</span><br><span>　　鼓励兴办小微企业。落实《国务院关于进一步支持小型微型企业健康发展的意见》（国发〔2012〕14号）、中国人民银行等5部委印发的《关于进一步深化小微企业金融服务的意见的通知》（银发〔2018〕162号）和省财政厅《关于进一步做好全省创业担保贷款财政贴息工作的通知》（赣财债〔2018〕92号），从财税、融资担保、社保补贴、创业担保贷款、公共服务等方面加大对小微企业支持力度。落实国家、省有关高校毕业生创业扶持政策，鼓励高校毕业生创办人力资源服务企业，对符合条件的创业担保贷款给予财政贴息。</span><br><span>　　支持人力资源服务企业兼并重组。对其在资产重组过程中，通过合并、分立、出售、置换等方式，将全部或部分实物资产以及与其相关联的债权、负债和劳动力一并转让给其他单位和个人，其中涉及的不动产、土地使用权转让行为，按规定不征收增值税。支持有条件的人力资源服务机构设立企业集团。支持企业通过连锁经营、加盟经营等方式，扩大服务范围。将人力资源服务企业上市纳入“映山红行动”。对在我省注册且总部设在我省的人力资源服务企业通过兼并、改制、借壳等方式在上海证券交易所或深圳证券交易所成功上市的，由省财政给予企业注册地所属设区市人民政府（含赣江新区管委会）一次性奖励500万元；在境外资本市场每新增一家省内上市人力资源服务企业，省财政按照实际融资金额2%，给予企业注册地所属设区市人民政府（含赣江新区管委会）一次性奖励，最高不超过500万元；对在境内重组上市（借壳）并完成注册地回迁的人力资源服务企业，参照在上海证券交易所、深圳证券交易所上市奖励政策执行。奖励资金专项用于推动企业上市工作。</span><br><span>　　鼓励人力资源服务企业做大做强。对在我省注册并获得“诚信服务示范机构”荣誉称号的人力资源服务企业，各地政府可根据其经营规模、业绩等方面情况，给予一定比例的贡献奖。</span><br><span>　　贯彻落实国家和省促进就业政策规定，确保经营性人力资源服务企业平等享受相关税收和补贴政策。</span><br><span>　　（二）推进人力资源服务产业园建设。</span><br><span>　　大力提升中国南昌人力资源服务产业园发展水平。根据南昌经济技术开发区、南昌高新技术产业开发区和南昌小蓝经济技术开发区不同的产业特点，对中国南昌人力资源服务产业园“一园三区”发展实行统一名称、统一规划、统一产业政策、统一信息平台、统一宣传策划；按照吸引多元化的人力资源服务企业、满足多层次的服务需求、具有专业化的服务能力、具备国际化的发展视野要求，将中国南昌人力资源服务产业园打造成为“立足南昌、惠及全省、辐射中部、面向全国”的区域性人力资源聚集高地和人才培养、人才储备的洼地，努力成为全国一流并与国际接轨的国家级人力资源服务产业园区。</span><br><span>　　积极推进省级人力资源服务产业园建设。支持建设一批有规模、有影响力、有辐射力的省级人力资源服务产业园。对新挂牌的省级人力资源服务产业园，省财政给予300万元的一次性开园补贴，所在地政府也应给予相应资助，并根据产业园运营情况，每年给予运营补贴。有市场需求但尚不具备建设省级产业园的市、县（区）可以设立市、县级人力资源服务产业园或人力资源服务产业指导中心，当地政府给予适当补贴。省级人力资源服务产业园评估认定工作由省人社厅负责，具体办法由省人社厅另行制定。</span><br><span>　　鼓励各人力资源服务产业园通过实施减免租金、贷款贴息、政府购买服务等优惠政策，吸引各类人力资源服务企业入驻。各地要通过设立公共服务窗口，完善服务功能，充分发挥园区培育、引进、展示、对接作用，促进人力资源服务业发展。</span><br><span>　　鼓励社会力量参与人力资源服务产业园建设。</span><br><span>　　（三）促进人力资源服务业态和产品创新。</span><br><span>　　开展“互联网+人力资源服务”行动，推动人力资源服务和互联网的深度融合，积极运用大数据、云计算等新技术，促进人力资源服务业创新融合发展。通过政府购买服务形式，鼓励有条件的高等院校、研发机构、人力资源服务企业、各产业园区加强人力资源服务理论、商业模式、新技术等方面的研究和应用。支持人力资源服务企业在做优做强传统项目的基础上，大力发展高级人才寻访、人力资源外包服务、人才测评、人力资源管理咨询、薪酬设计与管理、人力资源信息网络服务等新兴项目，探索开展与金融、教育、医疗等行业的跨界服务模式。人力资源服务企业为开发新技术、新产品发生的可纳入企业所得税税前加计扣除的研究开发费用，未形成无形资产计入当期损益的，在按照规定据实扣除的基础上，按研究开发费用的50%加计扣除；形成无形资产的，按无形资产成本的150%摊销。</span><br><span>　　支持建设人力资源服务孵化基地。通过市场化方式引进一批发展前景较好的新型业态和产品进行孵化，并提供客户对接和相关服务，帮助其在较短时间内成长为有一定规模和实力的人力资源服务企业并落地我省。鼓励人力资源服务孵化基地所在县（市、区）政府为入驻孵化基地的人力资源服务企业免费提供场地，并给予一定的水电费补贴。对经省人社厅认定的人力资源服务孵化基地，省财政一次性补助100万元。</span><br><span>　　（四）拓宽投融资渠道。</span><br><span>　　鼓励国有人力资源服务企业与非国有人力资源服务企业、非国有资本通过股权融合、联合投资、资源整合等方式在人力资源服务领域进行合作。鼓励建立包括混合所有制在内的多种形式的人力资源产业发展合作基金，撬动社会资本共同参与，促进人力资源服务业发展。鼓励各类融资担保机构加大对人力资源服务企业提供融资担保的力度。鼓励和引导本地金融机构加大对人力资源服务企业和重点人力资源服务项目的信贷资金投入，并在国家允许的贷款利率浮动幅度内给予一定的利率优惠。鼓励和引导人力资源服务企业在服务业风险补偿金模式下获取银行信贷支持。符合政策规定的，可申请创业贷款担保，并按规定给予贴息。</span><br><span>　　（五）加强行业高层次人才队伍建设。</span><br><span>　　制定实施人力资源服务业领军人才培养计划，建设一支素质优良、结构合理的人力资源服务业人才队伍。定期选派部分人力资源服务企业的中高级管理人员、高等院校及科研机构相关专业人员，到国（境）内外著名专业院校、知名人力资源服务企业学习培训，培训及往返交通费用由省财政全额补助；积极开展并鼓励人力资源服务机构参与人力资源服务业从业人员素质提升培训，培训费由就业专项资金支付。鼓励高等院校和有条件的技工院校设置人力资源服务相关专业，培养行业发展所需专业人才。加快引进一批具有国际背景、国际视野、通晓国际规则的行业高端人才，纳入我省高层次人才引进计划和人才引进项目，享受相关优惠政策。开辟职称评审“绿色通道”，对经国家认定的领军企业中的领军人才按规定认定相关高级专业技术职称。对本省或引进的全国性人力资源服务行业领军人才在我省服务期限满3年，在服务期内其服务的人力资源服务机构近三年作出较大贡献的，由省财政给予每人一次性奖励10万元。</span><br><span>　　（六）鼓励人力资源服务机构开展引才引智活动。</span><br><span>　　引导人力资源服务市场在引才引智方面发挥主渠道作用。鼓励用人单位通过人力资源服务机构开展高级人才寻访服务。对用人单位委托人力资源服务机构成功开展招才引智的，由同级财政给予寻访费补贴，其中引进中国科学院、中国工程院院士，每人补贴50万元；引进国际相关领域著名专家，“千人计划”“万人计划”“长江学者奖励计划”、国家杰出青年科学基金、中科院“百人计划”等国家重大人才工程入选人员，享受国务院特殊津贴的专家，每人补贴30万元；引进省部级重大人才工程入选人员，每人补贴15万元；引进具有正高级专业技术资格，且为我省经济社会发展急需紧缺的人员，或我省重点工程、重要支柱产业、重要新兴产业紧缺的企业高级管理人才、高技能人才，每人补贴5万元。鼓励人力资源服务机构为用人单位提供高级人才寻访服务，对人力资源服务机构为用人单位成功引进上述人才的，由同级财政分别按10万元、8万元、5万元、2万元标准给予一次性奖励。</span><br><span>　　（七）优化人力资源服务机构公共服务功能。</span><br><span>　　巩固和发展人力资源市场整合改革成果，进一步健全公共就业和人才服务体系，推进公共服务专业化、标准化和信息化建设。建立和完善公共服务平台，构建覆盖城乡的公共人力资源服务网络，提供“一站式”基本公共服务。</span><br><span>　　完善政府购买人力资源公共服务政策。将人力资源服务纳入政府购买服务目录，明确种类、性质、内容、条件和方式，通过竞争择优方式，支持和鼓励经营性人力资源服务机构参与公共就业人才服务活动、实施公共就业人才服务项目。</span><br><span>　　（八）搭建人力资源服务供需对接平台。</span><br><span>　　各地要通过多种形式，定期或不定期组织开展人力资源服务供需对接活动，集中推介人力资源服务产品，为人力资源服务机构与用人单位人力人才资源配置提供合作平台。</span><br><span>　　（九）加大对外开放和合作交流力度。</span><br><span>　　定期举办全国性人力资源高峰论坛、展示会、博览会等重大活动，加强与国内外人力资源服业知名机构和高端人才的交流，提升我省人力资源服务业的知名度和影响力，促进人力资源服务业转型升级。实施“一带一路”人力资源服务行动，鼓励有条件的本省人力资源服务机构“走出去”，与国际知名人力资源服务机构开展合作，在境外设立分支机构，大力开拓国际市场，按照国家相关政策，积极参与国际人才竞争与合作。</span><br><span>　　（十）加大财政资金投入。</span><br><span>　　设立省级人力资源服务业发展专项扶持资金。从2018年起，省财政每年预算安排资金2000万元，主要用于人力资源服务业机构品牌培育、知名企业和新兴项目引进、产业园建设、人才培养引进、人力资源服务孵化基地建设、合作交流、补贴奖励等方面。资金使用管理办法由省财政厅会同省人社厅另行制定。</span><br><span>　　各设区市每年要安排一定的资金用于支持人力资源服务业发展。</span><br><span>　　五、组织保障</span><br><span>　　（一）加强组织领导。促进人力资源服务业发展是一项系统工程，涉及面广。为加强工作协调，成立由省政府分管领导任组长，省政府分管副秘书长和省人社厅主要负责人为副组长，省委组织部、省发改委、省财政厅、省工信委、省税务局、省商务厅、省政府金融办、省工商局、省教育厅等部门有关负责人为成员的人力资源服务业发展领导小组，领导小组办公室设在省人社厅。省人社厅负责全省人力资源服务业发展具体协调工作。省级各相关部门要按照各自职责，密切配合，形成合力，共同做好组织推动工作。各级政府要加强对人力资源服务业发展的组织领导，建立相应的工作机构，将人力资源服务业发展工作纳入国民经济和经济社会发展规划，作为各级人才工作专项述职的重要内容，制订具体实施方案，切实抓好落实。</span><br><span>　　（二）加强协会建设。加强江西省人力资源服务业协会建设，充分发挥协会行业服务、行业代表、行业管理、行业协调、行业自律功能，推动行业公平竞争、诚信服务、自我约束、健康发展。支持行业协会参与标准化建设、行业信用评价、从业人员培训、高峰论坛活动举办、新兴业态管理监测等工作，加强行业发展理论研究，积极开展技术研讨和创新交流活动。</span><br><span>　　（三）加强行业监管。推动出台规范、促进我省人力资源市场发展的相关法规。落实“双随机、一公开”监管制度，依法实施行政许可，加大执法检查力度，规范人力资源市场秩序。落实人力资源服务机构年度报告公示制度，通过信息公示、定期抽查等方式，完善市场退出机制，构建权责明确、透明高效的事中事后监管机制。深入开展人力资源服务机构诚信主题创建行动，加强诚信体系建设，加快构建守信激励和失信惩罚的良好制度环境。</span><br><span>　　（四）加强信息服务。推进政府公共就业（人才）服务网站与经营性人力资源服务机构网站的联网贯通，形成一体化、多元化、全方位的区域性公共就业和人才信息服务系统，提高互联网信息技术在人力资源服务业中的运用能力。建立科学的人力资源服务业统计制度和信息发布机制，及时发布人才供求信息。建立经营性人力资源服务机构诚信经营信息与国家企业信用信息公示系统（江西）信息共享机制。</span><br><span>　　（五）加大宣传力度。充分利用各类媒体，广泛宣传发展人力资源服务业的重大意义和政策措施，及时总结推广各地各部门的好做法、好经验，努力营造全社会重视、关心、支持人力资源服务业发展的浓厚氛围。</span><br><span>　　2018年9月15日</span><br><span>　　（此件主动公开）</span></p>','政策法规',14,'1551925970'),
	(17,'劳务派遣暂行规定','','<p><span>劳务派遣用工是中国企业用工的补充形式，只能在临时性、辅助性或者替代性的工作岗位上实施。</span><br><span>为解决劳务派遣工同工不同酬、同岗不同权，社保福利待遇低，没有职业培训，职业发展受限等问题，打击违反临时性、辅助性、替代性的“三性”规定，滥用派遣制度等违法行为问题，作为贯彻落实《劳动合同法》的一项重要举措，人力资源和社会保障部研究起草了《劳务派遣暂行规定》（原名为《劳务派遣若干规定》），共7章、29条内容，主要就明确劳务派遣用工比例、辅助性岗位的确定程序、跨地区劳务派遣的社会保险、禁止“假外包真派遣”等重要内容作出规定。同时，《劳务派遣暂行规定》也对劳动合同、劳务派遣协议的订立、履行、解除和终止以及相应的法律责任等加以明确。</span><br><span>劳务派遣工与正式职工的待遇差距实际上就是工作过程中存在的最大安全事故隐患，从长远发展来看，不利于用工单位的长治久安。</span><br><span>劳务派遣工无限扩大不利于扩大党的执政基础。缩小贫富差距 应先把《劳务派遣暂行规定》贯彻落实到位，最终实现同工同酬。拒绝区别对待，规范劳务派遣，抵制劳务外包，外委。</span><br><span>第一章 总 则</span><br><span>第一条 为规范劳务派遣，维护劳动者的合法权益，促进劳动关系和谐稳定，依据《中华人民共和国劳动合同法》（以下简称劳动合同法）和《中华人民共和国劳动合同法实施条例》（以下简称劳动合同法实施条例）等法律、行政法规，制定本规定。</span><br><span>第二条 劳务派遣单位经营劳务派遣业务，企业（以下称用工单位）使用被派遣劳动者，适用本规定。</span><br><span>依法成立的会计师事务所、律师事务所等合伙组织和基金会以及民办非企业单位等组织使用被派遣劳动者，依照本规定执行。 [1-2]&nbsp;</span><br><span>第二章 用工范围和用工比例</span><br><span>第三条 用工单位只能在临时性、辅助性或者替代性的工作岗位上使用被派遣劳动者。</span><br><span>前款规定的临时性工作岗位是指存续时间不超过6个月的岗位；辅助性工作岗位是指为主营业务岗位提供服务的非主营业务岗位；替代性工作岗位是指用工单位的劳动者因脱产学习、休假等原因无法工作的一定期间内，可以由其他劳动者替代工作的岗位。</span><br><span>用工单位决定使用被派遣劳动者的辅助性岗位，应当经职工代表大会或者全体职工讨论，提出方案和意见，与工会或者职工代表平等协商确定，并在用工单位内公示。</span><br><span>第四条 用工单位应当严格控制劳务派遣用工数量，使用的被派遣劳动者数量不得超过其用工总量的10%。</span><br><span>前款所称用工总量是指用工单位订立劳动合同人数与使用的被派遣劳动者人数之和。</span><br><span>计算劳务派遣用工比例的用工单位是指依照劳动合同法和劳动合同法实施条例可以与劳动者订立劳动合同的用人单位。 [1-2]&nbsp;</span><br><span>第三章</span><br><span>劳动合同、劳务派遣协议的订立和履行</span><br><span>第五条 劳务派遣单位应当依法与被派遣劳动者订立2年以上的固定期限书面劳动合同。</span><br><span>第六条 劳务派遣单位可以依法与被派遣劳动者约定试用期。劳务派遣单位与同一被派遣劳动者只能约定一次试用期。</span><br><span>第七条 劳务派遣协议应当载明下列内容：</span><br><span>（一）派遣的工作岗位名称和岗位性质；</span><br><span>（二）工作地点；</span><br><span>（三）派遣人员数量和派遣期限；</span><br><span>（四）按照同工同酬原则确定的劳动报酬数额和支付方式；</span><br><span>（五）社会保险费的数额和支付方式；</span><br><span>（六）工作时间和休息休假事项；</span><br><span>（七）被派遣劳动者工伤、生育或者患病期间的相关待遇；</span><br><span>（八）劳动安全卫生以及培训事项；</span><br><span>（九）经济补偿等费用；</span><br><span>（十）劳务派遣协议期限；</span><br><span>（十一）劳务派遣服务费的支付方式和标准；</span><br><span>（十二）违反劳务派遣协议的责任；</span><br><span>（十三）法律、法规、规章规定应当纳入劳务派遣协议的其他事项。</span><br><span>第八条 劳务派遣单位应当对被派遣劳动者履行下列义务：</span><br><span>（一）如实告知被派遣劳动者劳动合同法第八条规定的事项、应遵守的规章制度以及劳务派遣协议的内容；</span><br><span>（二）建立培训制度，对被派遣劳动者进行上岗知识、安全教育培训；</span><br><span>（三）按照国家规定和劳务派遣协议约定，依法支付被派遣劳动者的劳动报酬和相关待遇；</span><br><span>（四）按照国家规定和劳务派遣协议约定，依法为被派遣劳动者缴纳社会保险费，并办理社会保险相关手续；</span><br><span>（五）督促用工单位依法为被派遣劳动者提供劳动保护和劳动安全卫生条件；</span><br><span>（六）依法出具解除或者终止劳动合同的证明；</span><br><span>（七）协助处理被派遣劳动者与用工单位的纠纷；</span><br><span>（八）法律、法规和规章规定的其他事项。</span><br><span>第九条 用工单位应当按照劳动合同法第六十二条规定，向被派遣劳动者提供与工作岗位相关的福利待遇，不得歧视被派遣劳动者。</span><br><span>第十条 被派遣劳动者在用工单位因工作遭受事故伤害的，劳务派遣单位应当依法申请工伤认定，用工单位应当协助工伤认定的调查核实工作。劳务派遣单位承担工伤保险责任，但可以与用工单位约定补偿办法。</span><br><span>被派遣劳动者在申请进行职业病诊断、鉴定时，用工单位应当负责处理职业病诊断、鉴定事宜，并如实提供职业病诊断、鉴定所需的劳动者职业史和职业危害接触史、工作场所职业病危害因素检测结果等资料，劳务派遣单位应当提供被派遣劳动者职业病诊断、鉴定所需的其他材料。</span><br><span>第十一条 劳务派遣单位行政许可有效期未延续或者《劳务派遣经营许可证》被撤销、吊销的，已经与被派遣劳动者依法订立的劳动合同应当履行至期限届满。双方经协商一致，可以解除劳动合同。</span><br><span>第十二条 有下列情形之一的，用工单位可以将被派遣劳动者退回劳务派遣单位：</span><br><span>（一）用工单位有劳动合同法第四十条第三项、第四十一条规定情形的；</span><br><span>（二）用工单位被依法宣告破产、吊销营业执照、责令关闭、撤销、决定提前解散或者经营期限届满不再继续经营的；</span><br><span>（三）劳务派遣协议期满终止的。</span><br><span>被派遣劳动者退回后在无工作期间，劳务派遣单位应当按照不低于所在地人民政府规定的最低工资标准，向其按月支付报酬。</span><br><span>第十三条 被派遣劳动者有劳动合同法第四十二条规定情形的，在派遣期限届满前，用工单位不得依据本规定第十二条第一款第一项规定将被派遣劳动者退回劳务派遣单位；派遣期限届满的，应当延续至相应情形消失时方可退回。 [1-2]&nbsp;</span><br><span>第四章 劳动合同的解除和终止</span><br><span>第十四条 被派遣劳动者提前30日以书面形式通知劳务派遣单位，可以解除劳动合同。被派遣劳动者在试用期内提前3日通知劳务派遣单位，可以解除劳动合同。劳务派遣单位应当将被派遣劳动者通知解除劳动合同的情况及时告知用工单位。</span><br><span>第十五条 被派遣劳动者因本规定第十二条规定被用工单位退回，劳务派遣单位重新派遣时维持或者提高劳动合同约定条件，被派遣劳动者不同意的，劳务派遣单位可以解除劳动合同。</span><br><span>被派遣劳动者因本规定第十二条规定被用工单位退回，劳务派遣单位重新派遣时降低劳动合同约定条件，被派遣劳动者不同意的，劳务派遣单位不得解除劳动合同。但被派遣劳动者提出解除劳动合同的除外。</span><br><span>第十六条 劳务派遣单位被依法宣告破产、吊销营业执照、责令关闭、撤销、决定提前解散或者经营期限届满不再继续经营的，劳动合同终止。用工单位应当与劳务派遣单位协商妥善安置被派遣劳动者。</span><br><span>第十七条 劳务派遣单位因劳动合同法第四十六条或者本规定第十五条、第十六条规定的情形，与被派遣劳动者解除或者终止劳动合同的，应当依法向被派遣劳动者支付经济补偿。 [1-2]&nbsp;</span><br><span>第五章 跨地区劳务派遣的社会保险</span><br><span>第十八条 劳务派遣单位跨地区派遣劳动者的，应当在用工单位所在地为被派遣劳动者参加社会保险，按照用工单位所在地的规定缴纳社会保险费，被派遣劳动者按照国家规定享受社会保险待遇。</span><br><span>第十九条 劳务派遣单位在用工单位所在地设立分支机构的，由分支机构为被派遣劳动者办理参保手续，缴纳社会保险费。</span><br><span>劳务派遣单位未在用工单位所在地设立分支机构的，由用工单位代劳务派遣单位为被派遣劳动者办理参保手续，缴纳社会保险费。 [1-2]&nbsp;</span><br><span>第六章 法律责任</span><br><span>第二十条 劳务派遣单位、用工单位违反劳动合同法和劳动合同法实施条例有关劳务派遣规定的，按照劳动合同法第九十二条规定执行。</span><br><span>第二十一条 劳务派遣单位违反本规定解除或者终止被派遣劳动者劳动合同的，按照劳动合同法第四十八条、第八十七条规定执行。</span><br><span>第二十二条 用工单位违反本规定第三条第三款规定的，由人力资源社会保障行政部门责令改正，给予警告；给被派遣劳动者造成损害的，依法承担赔偿责任。</span><br><span>第二十三条 劳务派遣单位违反本规定第六条规定的，按照劳动合同法第八十三条规定执行。</span><br><span>第二十四条 用工单位违反本规定退回被派遣劳动者的，按照劳动合同法第九十二条第二款规定执行。 [1-2]&nbsp;</span><br><span>第七章 附 则</span><br><span>第二十五条 外国企业常驻代表机构和外国金融机构驻华代表机构等使用被派遣劳动者的，以及船员用人单位以劳务派遣形式使用国际远洋海员的，不受临时性、辅助性、替代性岗位和劳务派遣用工比例的限制。</span><br><span>第二十六条 用人单位将本单位劳动者派往境外工作或者派往家庭、自然人处提供劳动的，不属于本规定所称劳务派遣。</span><br><span>第二十七条 用人单位以承揽、外包等名义，按劳务派遣用工形式使用劳动者的，按照本规定处理。</span><br><span>第二十八条 用工单位在本规定施行前使用被派遣劳动者数量超过其用工总量10%的，应当制定调整用工方案，于本规定施行之日起2年内降至规定比例。但是，《全国人民代表大会常务委员会关于修改〈中华人民共和国劳动合同法〉的决定》公布前已依法订立的劳动合同和劳务派遣协议期限届满日期在本规定施行之日起2年后的，可以依法继续履行至期限届满。</span><br><span>用工单位应当将制定的调整用工方案报当地人力资源社会保障行政部门备案。</span><br><span>用工单位未将本规定施行前使用的被派遣劳动者数量降至符合规定比例之前，不得新用被派遣劳动者。</span><br><span>第二十九条 本规定自2014年3月1日起施行。</span></p>','政策法规',72,'1551925993'),
	(18,'五险一金将建“黑名单”制度 这些行为会被惩戒','','<p><span>最近，“五险一金”迎来一个大变化，将会建立“黑名单”制度。以后，下面这些行为将被列入“黑名单”，受到联合惩戒，坐高铁、乘飞机、考公务员可能受到限制。</span></p><p><span>先来看社保“黑名单”</span><br><br><span>人社部日前制定了《社会保险领域严重失信“黑名单”管理暂行办法(征求意见稿)》，向社会公开征求意见，意见反馈截止时间为10月29日。</span><br><br></p><p><span>根据征求意见稿第5条规定，下面这6种情形拟列入社保“黑名单”——</span></p><p><span>6种情形将列入社保“黑名单”。<br>列入社保“黑名单”将受到哪些惩戒？<br><br>根据征求意见稿，各级社会保险经办机构应当按照有关规定，将社保“黑名单”信息纳入当地和全国信用信息共享平台。由相关部门在各自职责范围内依据《备忘录》规定，在政府采购、交通出行、招投标、生产许可、资质审核、融资贷款、市场准入、税收优惠、评优评先等方面予以限制。<br><br>社保“黑名单”惩戒期限有多久？<br><br>社保“黑名单”实行动态管理，纳入联合惩戒期限一般不超过5年。<br><br></span></p><p style=\"text-align: center;\"><img src=\"http://www.jxwlrl.com/up/pic/201810/31/20181031232451117661947.jpg\" alt=\"\"></p><p>再来看住房公积金“黑名单”。<br><br>住建部近期起草了《住房城乡建设领域失信联合惩戒对象名单管理暂行办法(网上征求意见稿)》，反馈截止时间为10月19日。<br><br>在住房公积金方面，以下6种情形将列入“黑名单”——</p><p></p><p style=\"text-align: center;\"><img src=\"http://www.jxwlrl.com/up/pic/201810/31/2018103123617908608181.jpg\" alt=\"\"></p><p>6种情形将列入住房公积金“黑名单”。<br><br>将受到哪些限制？<br><br>县级以上住房城乡建设主管部门应将已列入重点关注对象名单、失信联合惩戒对象名单的信用主体纳入重点监管对象，并在行政许可、招标投标、优惠政策等方面予以限制。<br><br>“黑名单”期限有多久？<br><br>失信联合惩戒对象和重点关注对象名单实施动态管理，有效期自公布之日起计算，一般为1年。<br><br>可见，对于个人来说，虚假参保、骗保、违规提取住房公积金、住房公积金贷款逾期，将会付出更大的代价！<br><br>对于企业来说，以后，不给员工缴“五险一金”也将会付出更多的代价！<br></p><p><span>现实生活中，一些企业为了减少成本，不给员工缴纳“五险一金”，而以后如果单位不给缴纳，且拒不整改，将被列入“黑名单”，一系列联合惩戒在等着它。</span><br><br><span>中国社科院世界社保研究中心执行研究员张盈华对中新网记者表示，这种“黑名单”机制有震慑力，让违反者付出信用代价，这是保护公平公正的有效机制。</span><br><br><span>张盈华认为，用人单位要认识到按时参保足额缴费是社保对职工权益的保障，是企业应尽的社会责任。同时，政府也应做好减税降负，让守法企业有长久生存和发展的好环境。</span><br><br><span>事实上，社保降费已经在路上了。10月22日召开的国务院常务会议提出，要进一步减轻企业税费负担。抓紧研究提出继续降低企业税负和降低社保费率的具体办法。</span></p>','政策法规',21,'1551926015');

